  recognition.onresult = function(event) {
  if (final_span.innerHTML = '') {
    document.getElementById("hinttext_span").innerHTML = 'Speech Now!';
    hinttext_span.innerHTML = 'Speech Now!';
  final_span.innerHTML = '';
  } else {
    final_span.innerHTML = '';
    document.getElementById("hinttext_span").innerHTML = '';
    hinttext_span.innerHTML = '';
    
  }
    var interim_transcript = '';
    if (typeof(event.results) == 'undefined') {
      recognition.onend = null;
      recognition.stop();
      upgrade();
      return;
    }
    for (var i = event.resultIndex; i < event.results.length; ++i) {
      if (event.results[i].isFinal) {
        final_transcript += event.results[i][0].transcript;
      } else {
        interim_transcript += event.results[i][0].transcript;
      }
    }
    final_transcript = capitalize(final_transcript);
    final_span.innerHTML = linebreak(final_transcript);
    interim_span.innerHTML = linebreak(interim_transcript);
    if (final_transcript || interim_transcript) {
      showButtons('inline-block');
    }
    if (final_transcript != '') {
      // Menampilkan hasil syntesis Final
      // console.log(final_transcript);
      window.final_output = final_transcript.toLowerCase();

    }
  };
